mapper.select:
SELECT h.ID,h.INTRODUCE,h.STYLE,h.CITY,h.STATUS,h.TITLE,h.HOUSE_URL,h.USER_ID,
h.LANDMARK,h.LON,h.LAT,u.PHONE,u.USERNAME
FROM HOUSE h INNER JOIN USER u ON h.USER_ID=u.ID

    and h.STATUS=#{status}
    and h.CITY like concat('%',#{city},'%')
    and TITLE like concat('%',#{title},'%')
    and LANDMARK like concat('%',#{landMark},'%')
    and PRICE >=#{minPrice}
    and PRICE <=#{maxPrice}

SELECT ID,INTRODUCE,STYLE,CITY,STATUS,TITLE,HOUSE_URL,USER_ID,LANDMARK,LON,LAT,
ROUND(6378.138 * 2 * ASIN(
SQRT(
POW(
SIN(
(
LAT * PI()/180-LAT * PI()/180
)/2
),
2
)+COS(LAT * PI()/180 * COS(LAT * PI()/180) * POW(
SIN(
(
LON * PI()/180-LON * PI()/180
)/2
),
2
))
) * 1000
) AS JULI
FROM HOUSE HAVING JULI>500 ORDER BY JULI ASC LIMIT 10

    LON=#{lon}
    AND LAT=#{lat}
